//Основной компонент,компанирует все элементы
import React from 'react';
import Info from './components/info'
import Form from './components/form'
import Weather from './components/weather'

const API_KEY="b6907d289e10d714a6e88b30761fae22";

class App extends React.Component {

  //async,await -асинхронно
  //fetch-возможность получать все со страницы
  gettingWeather= async ()=>{
   const api_url=await
       fetch(`https://samples.openweathermap.org/data/2.5/weather?q=London,uk&appid=${API_KEY}`);
  }

  render(){
    return(
        <div>
          <Info></Info>
          <Form></Form>
          <Weather></Weather>
        </div>
    );
  }
}

//экспортируем для подключения
export default App;
