//Основной компонент,компанирует все элементы
import React from 'react';
import Info from './components/info'
import Form from './components/form'
import Weather from './components/weather'

const API_KEY = "8c96a8adac5447629eb203740191507";

class App extends React.Component {

    state = {
        temp: undefined,
        city: undefined,
        country: undefined,
        error: undefined,
        time: undefined,
        dmy: undefined
    };

    onCitySubmit(e) {
        if(e.keyCode === 13) {
            this.gettingWeather(e.targer.value);
        }
    }

    //async,await -асинхронно
    //fetch-возможность получать все со страницы
    gettingWeather = async (e) => {

        e.preventDefault();
        const city = e.target.elements.city.value;

        if (city) {
            const api_url = await
                fetch(`https://api.apixu.com/v1/forecast.json?key=${API_KEY}&q=${city}&days=1`);
            const data = await api_url.json();
            const time = data.location.localtime.toString().slice(11, 18);
            const dmy = data.location.localtime.toString().slice(0, 10);
            document.addEventListener('keydown',this.onCitySubmit());
            this.setState({
                temp: data.current.temp_c,
                city: data.location.name,
                country: data.location.country,
                time: time,
                dmy: dmy,
                error: ""
            });
        } else {
            this.setState({
                temp: undefined,
                city: undefined,
                country: undefined,
                error: "Введите название города",
                time: undefined,
                dmy: undefined
            })
        }
    };

    render() {
        return (
            <div>
                <Info></Info>
                <div className="container">

                    <Form />
                    <Weather
                        temp={this.state.temp}
                        city={this.state.city}
                        country={this.state.country}
                        time={this.state.time}
                        dmy={this.state.dmy}
                        error={this.state.error}
                    />
                </div>
                <div className="input">
                    <input className="search" type='search' placeholder='Search location' onKeyPress={() => this.onCitySubmit()}/>
                </div>
            </div>

        );
    }
}

//экспортируем для подключения
export default App;
