import React from 'react';

const Weather=(props)=>(
    <div>
        {props.city &&
        <div>
            <p>Местоположение : {props.city},{props.country}</p>
            <p>Температура : {props.temp}</p>
            <p>Время : {props.localtime}</p>
        </div>
        }
        <p>{props.error}</p>
    </div>
);

export default Weather;
