//Основной компонент,компанирует все элементы
import React from 'react';
import Info from './components/info'
import Form from './components/form'
import Weather from './components/weather'

const API_KEY = "29642";
const convert = require('xml-js');
class App extends React.Component {

    //async,await -асинхронно
    //fetch-возможность получать все со страницы
    gettingWeather = async (e) => {

        e.preventDefault();
        const city=e.target.elements.city.value;
        const api_url = await
            fetch(`"http://export.yandex.ru/weather-ng/forecasts/${API_KEY}.xml"`);
        const data = await convert.api_url.json2;
        console.log(data);
    };

    render() {
        return (
            <div>
                <Info></Info>
                <Form weatherMethod={this.gettingWeather} />
                <Weather></Weather>
            </div>
        );
    }
}

//экспортируем для подключения
export default App;
