import React from 'react';

const Weather=(props)=>(

    <div>
        <h4>Узнайте погоду в вашем гроде</h4>
        {props.city &&
        <div>
            <p>Местоположение : {props.city},{props.country}</p>
            <p>Температура : {props.temp}</p>
            <p>Время : {props.time}</p>
            <p>ГГ-ММ-ДД : {props.dmy}</p>
        </div>
        }
        <p>{props.error}</p>
    </div>
);

export default Weather;
