//Основной компонент,компанирует все элементы
import React from 'react';
import Info from './components/info'
import Form from './components/form'
import Weather from './components/weather'

const API_KEY = "b6907d289e10d714a6e88b30761fae22";

class App extends React.Component {

    //async,await -асинхронно
    //fetch-возможность получать все со страницы
    gettingWeather = async (e) => {
        e.preventDefault;
        const api_url = await
            fetch(`https://samples.openweathermap.org/data/2.5/weather?q=London&appid=${API_KEY}`);
        const data = await api_url.json();
        console.log(data);
    };

    render() {
        return (
            <div>
                <Info></Info>
                <Form weatherMethod={this.gettingWeather}></Form>
                <Weather></Weather>
            </div>
        );
    }
}

//экспортируем для подключения
export default App;
